#!/bin/bash
    find $1 -type f -iname \*.h -exec sh -c  "python cpplint.py  --filter=+build,-build/include,+readability,-readability/multiline_comment,-readability/function,+runtime,-runtime/int,-runtime/printf,-runtime/rtti,-whitespace/braces,-whitespace/line_length,-whitespace/comments,-whitespace/parens --output=vs7 '{}'" \; 

    find $1 -type f -iname \*.cpp -exec sh -c  "python cpplint.py  --filter=+build,-build/include,+readability,-readability/multiline_comment,-readability/function,+runtime,-runtime/int,-runtime/printf,-runtime/rtti,-whitespace/braces,-whitespace/line_length,-whitespace/comments,-whitespace/parens --output=vs7 '{}'" \; 